const io = require('console-read-write');

async function main()
{
    io.write('Введите n');
    let n=await io.read();
    io.write('введите последовательность')
    let c =await io.read();
    let count = Number(0);
    for(let i=0;i<n*2;i++){
        if(c[i]=='(')
            count++;
        else
            count--;
    }
    if(count==0)
        io.write('OK!');
    else
        io.write('not OK!');
}

main();